mapper.update:
UPDATE orders
set orders.g_name=#{name}
WHERE orders.g_id=#{gid}

mapper.select:
      select g.*
        from
        goods g,
        goods_goodstag gg
where gg.tid = ${vo.tid} and g.id =gg.gid

    and g.title like '${vo.title}'


    and g.current_price >= ${vo.pricelow}


            and g.current_price  <= ${vo.pricehigh}


            and g.status = ${vo.status}


            and g.gmt_create >= ${vo.startTime}


            and g.gmt_create <= ${vo.endTime}


            and g.stock >= ${vo.num}




                order by price desc


                order by price 



            order by buy_num desc
